import React, { Component } from 'react'

export default class RenderingCondition extends Component {
    login=true;
    userName = "Pham Xuan Manh";
  render() {
    return (
      <div>
        {this.login ? <p>Hello {this.userName}</p> : <button>Login</button>}
      </div>
    )
  }
}
